<?php
/**
 * XAMPP Root Index
 * Generated by xampp-tools
 * Lists all configured virtual hosts and provides quick access
 */

// Load configuration
$vhosts_file = '{{XAMPP_ROOT_DIR}}\\..\\xampp-tools\\config\\vhosts.json';
$vhosts = [];

if (file_exists($vhosts_file)) {
    $vhosts_data = json_decode(file_get_contents($vhosts_file), true);
    if (isset($vhosts_data['vhosts'])) {
        $vhosts = $vhosts_data['vhosts'];
    }
}

// Determine VHOSTS_EXTENSION (default to .local)
$vhosts_ext = '.local';

// Get server info
$server_name = $_SERVER['SERVER_NAME'] ?? 'localhost';
$server_port = $_SERVER['SERVER_PORT'] ?? '80';
$protocol = (!empty($_SERVER['HTTPS']) && $_SERVER['HTTPS'] !== 'off') ? 'https' : 'http';
$base_url = "{$protocol}://{$server_name}";

?>
